import React from 'react';
import './ClientTestimonials.css'; // Import CSS for additional styling if needed

function ClientTestimonials() {
    // Example testimonials data
    const testimonials = [
        {
            name: 'John Doe',
            feedback: 'Great service! Highly recommend.',
            image: '/path/to/image1.jpg',
        },
        {
            name: 'Jane Smith',
            feedback: 'Amazing experience with the team!',
            image: '/path/to/image2.jpg',
        },
        {
            name: 'Michael Johnson',
            feedback: 'Professional and efficient.',
            image: '/path/to/image3.jpg',
        },
        {
            name: 'Emily Davis',
            feedback: 'Exceeded my expectations!',
            image: '/path/to/image4.jpg',
        },
        {
            name: 'David Brown',
            feedback: 'Will definitely come back.',
            image: '/path/to/image5.jpg',
        },
        {
            name: 'Sarah Wilson',
            feedback: 'A pleasure to work with.',
            image: '/path/to/image6.jpg',
        },
    ];

    return (
        <section className="client-testimonials py-5">
            <div className="container">
                <h2 className="text-center mb-4">What Our Clients Say</h2>
                <div className="row">
                    {testimonials.map((testimonial, index) => (
                        <div className="col-md-4 mb-4" key={index}>
                            <div className="card">
                                <img
                                    src={testimonial.image}
                                    className="card-img-top"
                                    alt={`${testimonial.name}'s testimonial`}
                                />
                                <div className="card-body">
                                    <h5 className="card-title">{testimonial.name}</h5>
                                    <p className="card-text">{testimonial.feedback}</p>
                                </div>
                            </div>
                        </div>
                    ))}
                </div>
            </div>
        </section>
    );
}

export default ClientTestimonials;
