import React from 'react';

function JobOpenings() {
    const jobs = [
        {
            title: 'Software Engineer',
            department: 'Engineering',
            location: 'Remote/Hyderabad',
            jobType: 'Full-time',
            description: 'We are looking for a passionate software engineer to join our team. You will be responsible for developing high-quality applications.'
        },
        // Add more job data here
    ];

    return (
        <section className="job-openings py-5">
            <div className="container">
                <h2 className="text-center mb-4">Current Job Openings</h2>
                <div className="row">
                    {jobs.map((job, index) => (
                        <div className="col-md-4 mb-4" key={index}>
                            <div className="card h-100">
                                <img src="job.jpg" className="card-img-top" alt={job.title} />
                                <div className="card-body">
                                    <h5 className="card-title">{job.title}</h5>
                                    <p className="card-text"><strong>Department:</strong> {job.department}</p>
                                    <p className="card-text"><strong>Location:</strong> {job.location}</p>
                                    <p className="card-text"><strong>Job Type:</strong> {job.jobType}</p>
                                    <p className="card-text">{job.description}</p>
                                    <a href="#" className="btn btn-link p-0">Read More</a>
                                </div>
                                <div className="card-footer bg-transparent">
                                    <a href="#" className="btn btn-primary btn-block">Apply Now</a>
                                </div>
                            </div>
                        </div>
                    ))}
                </div>
            </div>
        </section>
    );
}

export default JobOpenings;
